        colortext = "#00B2A5";
      }
      if (colorindex === 2) {
        colortext = "#99B2A5";
      }
      if (colorindex === 3) {
        colortext = "#CCB2A5";
      }
      let halfhoureventwidth = parseInt(100 / halfhourevent.length) + "%";
      let evtDisplay =
        halfhourevent[j].id +
        "-" +
        halfhourevent[j].startdate +
        "-" +
        halfhourevent[j].enddate;
      halfhoureventHtml.push(
        <div
          key={j}
          style={{
            position: "relative",
            width: halfhoureventwidth,
          }}
        >
          <div
            style={{
              border: "1px solid black",
              position: "absolute",
              top: startminppercentage,
              width: "100%",
              height: durationhourppercentage,
              backgroundColor: colortext,
              zIndex: zedIndex,
            }}
          >
            <div
              style={{
                width: "50%",
                overflow: "hidden",
                whiteSpace: "nowrap",
                margin: "auto",
              }}
