              onClick={(e) =>
                this.halfHourBlockClick({
                  e: e,
                  eventdata: halfhourevent[j],
                  datetime: "",
                  hour: "",
                })
              }
              title={evtDisplay}
            >
              {evtDisplay}
            </div>
          </div>
        </div>
      );
      zedIndex = zedIndex + 1;
      colorindex = colorindex + 1;
      if (colorindex === 4) {
        colorindex = 1;
      }
    }
    console.log(halfhoureventHtml);
    return halfhoureventHtml;
  };

