import React, { Component } from "react";
import { GetLocalIcon, GetGoogleIcon } from "./icons";
import { getRecorddata } from "../db/index";
import Recorddataeditview from "./recorddataeditview2";

import {
  getMonthStringDisplay,
  getMonthDigitalDisplay,
  getDateDigitalDisplay,
  getDayStringDisplay,
  getUniqueArray,
  getLocalData,
  filterArraybyObject,
} from "../js/index";
import localdbdata from "../db/localdb/calenderdata.json";

class F extends Component {
  constructor(props) {
    super(props);
    let d = new Date();

    let month = (d.getMonth() + 1).toString();
    if (month.length === 1) {
      month = "0" + month;
    }
    let date = d.getDate().toString();
    if (date.length === 1) {
      date = "0" + date;
    }

    let year = d.getFullYear();

    this.state = {
      compType: "list",
      viewMeetingData: {
        attendeeuserid: {},
        body: "",
        class: "",
        enddate: "",
        hostuserid: {},
        internetaccessdata: {
          meetingid: "",
          passcode: "",
          url: "",
        },
        label: "",
        locationdata: {
          fulladdress: "",
          gocode: "",
        },
        organizeruserid: "",
        orgname: "",
        phoneaccessdata: {
          dialnumber: "",
          dialpasscode: "",
        },
        recurrency: {
          enddate: "",
          isrecurring: "",
          startdate: "",
          type: "daily",
        },
        startdate: "",
        subject: "",
        type: "zoommeet,orgmeet",
      },
      viewtype: "month",
      currentdate: date,
      currentmonth: month,
      currentyear: year,
      activedate: date,
      activemonth: month,
      activeyear: year,
      eventdata: [],
      viewStartDate: "",
      viewEndDate: "",
      viewTypeMonthRetrievalStartDate1: "",
      viewTypeMonthRetrievalStartDate2: "",
      viewTypeWeekRetrievalStartDate1: "",
      viewTypeWeekRetrievalStartDate2: "",
      defaultNewMeetingDate: "",
      defaultNewMeetingHour: "",
    };
  }

  async componentDidMount() {
    console.log(this.props);
    let {
      tablename,
      comphtmlid,
      recordeditviewprops,
      showallrecordsubscribe,
      parentobjectfilter,
      showallrecords,
      enablelocaldbdata,
      listdisplaytype,
      defaultdatafilter,
      defaultsortparam,
      parentid,
      iscreateactive,
      isviewactive,
      hidesidebar,
      hidedisplaytypepanel,
    } = this.props.compprops;
    console.log(comphtmlid);
    console.log(comprecordeditviewpropshtmlid);
    console.log(listdisplaytype);
    console.log(defaultdatafilter);
    console.log(iscreateactive);
    console.log(iscreateactive);
    console.log(isviewactive);
    console.log(hidesidebar);
    console.log(hidedisplaytypepanel);

    let { activedate, activemonth, activeyear, viewtype } = this.state;

    let viewStartDate,
      viewEndDate,
      viewTypeWeekRetrievalStartDate1,
      viewTypeWeekRetrievalStartDate2,
      viewTypeMonthRetrievalStartDate1,
      viewTypeMonthRetrievalStartDate2;

    if (viewtype === "week") {
      let d2 = new Date();
      let d2day = d2.getDay();
      let d2date = d2.getDate();
      d2.setDate(d2date - d2day);

      let d3 = new Date();
      let d3day = d3.getDay();
      let d3date = d3.getDate();
      d3.setDate(d3date + 6 - d3day);

      viewStartDate = d2;
      viewEndDate = d3;
      viewTypeWeekRetrievalStartDate1 = d2;
      viewTypeWeekRetrievalStartDate2 = d3;
    }

    if (viewtype === "month") {
      let d = new Date();
      d.setDate(1);

      let d1 = new Date();
      d1.setDate(0);
      d1.setDate(1);

      let d2 = new Date();
      d2.setDate(1);

      let d3 = new Date();
      let d3month = d3.getMonth();
      d3month = d3month + 1;
      d3.setMonth(d3month);
      d3.setDate(0);

      viewStartDate = d2;
      viewEndDate = d3;
      viewTypeMonthRetrievalStartDate1 = d1;
      viewTypeMonthRetrievalStartDate2 = d;
    }
    this.getRecordListMetadataAndDatafromServer({
      tableName: tablename,
      defaultsortparam: defaultsortparam,
      targetdate: activedate,
      targetmonth: activemonth,
      targetyear: activeyear,
      viewtype: viewtype,
      enablelocaldbdata: enablelocaldbdata,
      viewStartDate: viewStartDate,
      viewEndDate: viewEndDate,
      viewTypeWeekRetrievalStartDate1: viewTypeWeekRetrievalStartDate1,
      viewTypeWeekRetrievalStartDate2: viewTypeWeekRetrievalStartDate2,
      viewTypeMonthRetrievalStartDate1: viewTypeMonthRetrievalStartDate1,
